import java.util.Scanner;

public class num_1001 {

	public static int minus() {
		Scanner scan = new Scanner(System.in);
		int a = scan.nextInt();
		int b = scan.nextInt();
		
		return a - b;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(minus());
	}

}
